import React from "react";

import { AnalyticsFilters } from "../models/analytics";

interface AnalyticsFiltersComponentProps {
  filters: AnalyticsFilters;
  onFiltersChange: (filters: AnalyticsFilters) => void;
}

const AnalyticsFiltersComponent: React.FC<AnalyticsFiltersComponentProps> = ({
  filters,
  onFiltersChange,
}) => {
  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <h3 className="text-lg font-semibold mb-4">Analytics Filters</h3>
      <p className="text-gray-600">
        Active filters: {Object.keys(filters).length}.
        <button
          onClick={() => onFiltersChange(filters)}
          className="text-blue-600 ml-2"
        >
          Refresh Filters
        </button>
      </p>
    </div>
  );
};

export default AnalyticsFiltersComponent;

// src/components/AnalyticsWidgets.tsx - Specialized analytics and reporting components
import clsx from "clsx";
import {
  TrendingUp,
  TrendingDown,
  Target,
  DollarSign,
  Users,
  Calendar,
  Download,
  RefreshCw,
  MoreHorizontal,
} from "lucide-react";
import React, { useState } from "react";

// KPI Widget Component
interface KPIWidgetProps {
  title: string;
  value: number | string;
  previousValue?: number;
  format?: "currency" | "percentage" | "number";
  trend?: "up" | "down" | "neutral";
  target?: number;
  icon?: React.ReactNode;
  color?: "blue" | "green" | "purple" | "yellow" | "red";
  className?: string;
}

export const KPIWidget: React.FC<KPIWidgetProps> = ({
  title,
  value,
  previousValue,
  format = "number",
  trend,
  target,
  icon,
  color = "blue",
  className = "",
}) => {
  const formatValue = (val: number | string) => {
    if (typeof val === "string") return val;

    switch (format) {
      case "currency":
        return `$${val.toLocaleString()}`;
      case "percentage":
        return `${val.toFixed(1)}%`;
      default:
        return val.toLocaleString();
    }
  };

  const calculateChange = () => {
    if (typeof value !== "number" || !previousValue) return null;
    const change = ((value - previousValue) / previousValue) * 100;
    return change;
  };

  const change = calculateChange();
  const colorClasses = {
    blue: "from-blue-500 to-blue-600",
    green: "from-green-500 to-green-600",
    purple: "from-purple-500 to-purple-600",
    yellow: "from-yellow-500 to-yellow-600",
    red: "from-red-500 to-red-600",
  };

  const iconBgColors = {
    blue: "bg-blue-500/20 text-blue-400",
    green: "bg-green-500/20 text-green-400",
    purple: "bg-purple-500/20 text-purple-400",
    yellow: "bg-yellow-500/20 text-yellow-400",
    red: "bg-red-500/20 text-red-400",
  };

  return (
    <div className={clsx("card-base p-6 relative overflow-hidden", className)}>
      {/* Background gradient */}
      <div
        className={clsx(
          "absolute inset-0 bg-gradient-to-br opacity-5",
          colorClasses[color],
        )}
      />

      <div className="relative">
        <div className="flex items-start justify-between mb-4">
          <div>
            <p className="text-sm font-medium text-slate-400 mb-1">{title}</p>
            <p className="text-3xl font-bold text-white">
              {formatValue(value)}
            </p>
          </div>

          {icon && (
            <div className={clsx("p-3 rounded-xl", iconBgColors[color])}>
              {icon}
            </div>
          )}
        </div>

        <div className="space-y-3">
          {/* Change indicator */}
          {change !== null && (
            <div className="flex items-center space-x-2">
              <div
                className={clsx(
                  "flex items-center space-x-1 px-2 py-1 rounded-full text-xs font-medium",
                  change > 0
                    ? "bg-green-500/20 text-green-400"
                    : change < 0
                      ? "bg-red-500/20 text-red-400"
                      : "bg-slate-500/20 text-slate-400",
                )}
              >
                {change > 0 ? (
                  <TrendingUp className="w-3 h-3" />
                ) : change < 0 ? (
                  <TrendingDown className="w-3 h-3" />
                ) : (
                  <div className="w-3 h-3" />
                )}
                <span>{Math.abs(change).toFixed(1)}%</span>
              </div>
              <span className="text-xs text-slate-500">vs previous period</span>
            </div>
          )}

          {/* Progress bar for target */}
          {target && typeof value === "number" && (
            <div>
              <div className="flex justify-between text-xs text-slate-400 mb-1">
                <span>Progress to target</span>
                <span>{((value / target) * 100).toFixed(1)}%</span>
              </div>
              <div className="w-full bg-slate-700/50 rounded-full h-2">
                <div
                  className={clsx(
                    "h-2 rounded-full bg-gradient-to-r",
                    colorClasses[color],
                  )}
                  style={{ width: `${Math.min((value / target) * 100, 100)}%` }}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Campaign Performance Summary
interface CampaignSummaryProps {
  campaigns: {
    id: string;
    name: string;
    goal: number;
    raised: number;
    donors: number;
    daysLeft: number;
    status: "active" | "completed" | "paused";
  }[];
  className?: string;
}

export const CampaignPerformanceSummary: React.FC<CampaignSummaryProps> = ({
  campaigns,
  className = "",
}) => {
  const [sortBy, setSortBy] = useState<"progress" | "raised" | "donors">(
    "progress",
  );

  const sortedCampaigns = [...campaigns].sort((a, b) => {
    switch (sortBy) {
      case "progress":
        return b.raised / b.goal - a.raised / a.goal;
      case "raised":
        return b.raised - a.raised;
      case "donors":
        return b.donors - a.donors;
      default:
        return 0;
    }
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active":
        return "bg-green-500/20 text-green-400";
      case "completed":
        return "bg-blue-500/20 text-blue-400";
      case "paused":
        return "bg-yellow-500/20 text-yellow-400";
      default:
        return "bg-slate-500/20 text-slate-400";
    }
  };

  return (
    <div className={clsx("card-base", className)}>
      <div className="p-6 border-b border-slate-700/50">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-white">
              Campaign Performance
            </h3>
            <p className="text-sm text-slate-400">
              {campaigns.length} active campaigns
            </p>
          </div>

          <div className="flex items-center space-x-3">
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as any)}
              className="input-base text-sm py-2 px-3"
            >
              <option value="progress">Sort by Progress</option>
              <option value="raised">Sort by Amount Raised</option>
              <option value="donors">Sort by Donor Count</option>
            </select>

            <button className="p-2 text-slate-400 hover:text-white hover:bg-slate-700/50 rounded-lg transition-colors">
              <RefreshCw className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>

      <div className="p-6 space-y-4">
        {sortedCampaigns.map((campaign) => {
          const progress = (campaign.raised / campaign.goal) * 100;

          return (
            <div
              key={campaign.id}
              className="p-4 bg-slate-800/30 rounded-xl hover:bg-slate-700/30 transition-colors cursor-pointer"
            >
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1">
                  <div className="flex items-center space-x-3">
                    <h4 className="font-medium text-white">{campaign.name}</h4>
                    <span
                      className={clsx(
                        "px-2 py-0.5 rounded-full text-xs font-medium",
                        getStatusColor(campaign.status),
                      )}
                    >
                      {campaign.status}
                    </span>
                  </div>
                  <div className="flex items-center space-x-4 mt-2 text-sm text-slate-400">
                    <span>${campaign.raised.toLocaleString()} raised</span>
                    <span>{campaign.donors} donors</span>
                    <span>{campaign.daysLeft}d left</span>
                  </div>
                </div>

                <div className="text-right">
                  <div className="text-lg font-semibold text-white">
                    {progress.toFixed(1)}%
                  </div>
                  <div className="text-sm text-slate-400">
                    of ${campaign.goal.toLocaleString()}
                  </div>
                </div>
              </div>

              <div className="w-full bg-slate-700/50 rounded-full h-2">
                <div
                  className="h-2 rounded-full bg-gradient-to-r from-blue-500 to-purple-500"
                  style={{ width: `${Math.min(progress, 100)}%` }}
                />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// Donation Trends Chart
interface DonationTrendsProps {
  data: {
    period: string;
    donations: number;
    amount: number;
  }[];
  timeframe: "daily" | "weekly" | "monthly";
  onTimeframeChange: (timeframe: "daily" | "weekly" | "monthly") => void;
  className?: string;
}

export const DonationTrends: React.FC<DonationTrendsProps> = ({
  data,
  timeframe,
  onTimeframeChange,
  className = "",
}) => {
  const [metric, setMetric] = useState<"amount" | "count">("amount");

  const maxValue = Math.max(
    ...data.map((d) => (metric === "amount" ? d.amount : d.donations)),
  );

  return (
    <div className={clsx("card-base p-6", className)}>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-lg font-semibold text-white">Donation Trends</h3>
          <p className="text-sm text-slate-400">
            {metric === "amount"
              ? "Total donation amounts"
              : "Number of donations"}{" "}
            over time
          </p>
        </div>

        <div className="flex items-center space-x-3">
          <div className="flex bg-slate-800/50 rounded-xl p-1">
            <button
              onClick={() => setMetric("amount")}
              className={clsx(
                "px-3 py-1.5 rounded-lg text-sm font-medium transition-all",
                metric === "amount"
                  ? "bg-blue-500/20 text-blue-300"
                  : "text-slate-400 hover:text-white",
              )}
            >
              Amount
            </button>
            <button
              onClick={() => setMetric("count")}
              className={clsx(
                "px-3 py-1.5 rounded-lg text-sm font-medium transition-all",
                metric === "count"
                  ? "bg-blue-500/20 text-blue-300"
                  : "text-slate-400 hover:text-white",
              )}
            >
              Count
            </button>
          </div>

          <div className="flex bg-slate-800/50 rounded-xl p-1">
            {(["daily", "weekly", "monthly"] as const).map((tf) => (
              <button
                key={tf}
                onClick={() => onTimeframeChange(tf)}
                className={clsx(
                  "px-3 py-1.5 rounded-lg text-sm font-medium transition-all capitalize",
                  timeframe === tf
                    ? "bg-purple-500/20 text-purple-300"
                    : "text-slate-400 hover:text-white",
                )}
              >
                {tf}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="h-64 flex items-end justify-between space-x-2">
        {data.map((item, index) => {
          const value = metric === "amount" ? item.amount : item.donations;
          const height = (value / maxValue) * 100;

          return (
            <div
              key={index}
              className="flex-1 flex flex-col items-center space-y-2 group"
            >
              <div className="relative w-full">
                <div
                  className="w-full bg-gradient-to-t from-blue-600 to-purple-500 rounded-t-sm transition-all duration-300 group-hover:from-blue-500 group-hover:to-purple-400 cursor-pointer"
                  style={{ height: `${Math.max(height, 4)}px` }}
                >
                  {/* Tooltip */}
                  <div className="absolute -top-12 left-1/2 -translate-x-1/2 bg-slate-800/95 text-white text-xs px-3 py-2 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap z-10">
                    {metric === "amount"
                      ? `$${item.amount.toLocaleString()}`
                      : `${item.donations} donations`}
                  </div>
                </div>
              </div>
              <span className="text-xs text-slate-400 text-center">
                {item.period}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// Export Widget
interface ExportWidgetProps {
  onExport: (
    format: string,
    dateRange?: { start: string; end: string },
  ) => void;
  className?: string;
}

export const ExportWidget: React.FC<ExportWidgetProps> = ({
  onExport,
  className = "",
}) => {
  const [showOptions, setShowOptions] = useState(false);
  const [format, setFormat] = useState("csv");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });

  const formats = [
    { value: "csv", label: "CSV", description: "Excel compatible" },
    { value: "pdf", label: "PDF", description: "Report format" },
    { value: "json", label: "JSON", description: "API format" },
  ];

  const handleExport = () => {
    onExport(format, dateRange.start && dateRange.end ? dateRange : undefined);
    setShowOptions(false);
  };

  return (
    <div className={clsx("card-base p-4", className)}>
      <div className="flex items-center justify-between">
        <div>
          <h3 className="font-medium text-white">Export Data</h3>
          <p className="text-sm text-slate-400">
            Download campaign and donor data
          </p>
        </div>

        <div className="flex items-center space-x-2">
          <button
            onClick={() => setShowOptions(!showOptions)}
            className="p-2 text-slate-400 hover:text-white hover:bg-slate-700/50 rounded-lg transition-colors"
          >
            <MoreHorizontal className="w-4 h-4" />
          </button>

          <button
            onClick={handleExport}
            className="button-primary flex items-center space-x-2"
          >
            <Download className="w-4 h-4" />
            <span>Export</span>
          </button>
        </div>
      </div>

      {showOptions && (
        <div className="mt-4 pt-4 border-t border-slate-700/50 space-y-4 animate-slide-up">
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">
              Format
            </label>
            <div className="grid grid-cols-3 gap-2">
              {formats.map((fmt) => (
                <button
                  key={fmt.value}
                  onClick={() => setFormat(fmt.value)}
                  className={clsx(
                    "p-3 text-left rounded-lg border transition-all",
                    format === fmt.value
                      ? "border-blue-500/50 bg-blue-500/10 text-blue-300"
                      : "border-slate-700/50 bg-slate-800/30 text-slate-300 hover:bg-slate-700/30",
                  )}
                >
                  <div className="font-medium">{fmt.label}</div>
                  <div className="text-xs opacity-75">{fmt.description}</div>
                </button>
              ))}
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                Start Date
              </label>
              <input
                type="date"
                value={dateRange.start}
                onChange={(e) =>
                  setDateRange((prev) => ({ ...prev, start: e.target.value }))
                }
                className="input-base text-sm"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                End Date
              </label>
              <input
                type="date"
                value={dateRange.end}
                onChange={(e) =>
                  setDateRange((prev) => ({ ...prev, end: e.target.value }))
                }
                className="input-base text-sm"
                min={dateRange.start}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Real-time Activity Feed
interface Activity {
  id: string;
  type: "donation" | "new_donor" | "campaign_update" | "goal_reached";
  message: string;
  timestamp: Date;
  amount?: number;
  campaign?: string;
  donor?: string;
}

interface ActivityFeedProps {
  activities: Activity[];
  maxItems?: number;
  className?: string;
}

export const ActivityFeed: React.FC<ActivityFeedProps> = ({
  activities,
  maxItems = 10,
  className = "",
}) => {
  const getActivityIcon = (type: string) => {
    switch (type) {
      case "donation":
        return <DollarSign className="w-4 h-4 text-green-400" />;
      case "new_donor":
        return <Users className="w-4 h-4 text-blue-400" />;
      case "campaign_update":
        return <Target className="w-4 h-4 text-purple-400" />;
      case "goal_reached":
        return <TrendingUp className="w-4 h-4 text-yellow-400" />;
      default:
        return <div className="w-4 h-4 bg-slate-400 rounded-full" />;
    }
  };

  const getActivityColor = (type: string) => {
    switch (type) {
      case "donation":
        return "border-green-500/30 bg-green-500/5";
      case "new_donor":
        return "border-blue-500/30 bg-blue-500/5";
      case "campaign_update":
        return "border-purple-500/30 bg-purple-500/5";
      case "goal_reached":
        return "border-yellow-500/30 bg-yellow-500/5";
      default:
        return "border-slate-500/30 bg-slate-500/5";
    }
  };

  const formatTimeAgo = (timestamp: Date) => {
    const now = new Date();
    const diff = now.getTime() - timestamp.getTime();
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(diff / (1000 * 60 * 60));

    if (minutes < 1) return "Just now";
    if (minutes < 60) return `${minutes}m ago`;
    if (hours < 24) return `${hours}h ago`;
    return timestamp.toLocaleDateString();
  };

  const displayedActivities = activities.slice(0, maxItems);

  return (
    <div className={clsx("card-base", className)}>
      <div className="p-6 border-b border-slate-700/50">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-white">
              Recent Activity
            </h3>
            <p className="text-sm text-slate-400">
              Live updates from your campaigns
            </p>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
            <span className="text-xs text-green-400">Live</span>
          </div>
        </div>
      </div>

      <div className="max-h-96 overflow-y-auto custom-scrollbar">
        {displayedActivities.length > 0 ? (
          <div className="p-4 space-y-3">
            {displayedActivities.map((activity, index) => (
              <div
                key={activity.id}
                className={clsx(
                  "p-4 rounded-xl border transition-all duration-200 hover:shadow-md",
                  getActivityColor(activity.type),
                  index === 0 && "animate-fade-in",
                )}
              >
                <div className="flex items-start space-x-3">
                  <div className="flex-shrink-0 p-2 bg-slate-800/50 rounded-lg">
                    {getActivityIcon(activity.type)}
                  </div>

                  <div className="flex-1 min-w-0">
                    <p className="text-sm text-white leading-relaxed">
                      {activity.message}
                      {activity.amount && (
                        <span className="font-semibold text-green-400 ml-1">
                          ${activity.amount.toLocaleString()}
                        </span>
                      )}
                    </p>

                    <div className="flex items-center justify-between mt-2">
                      <div className="flex items-center space-x-2 text-xs text-slate-400">
                        {activity.campaign && (
                          <span className="px-2 py-0.5 bg-slate-700/50 rounded-full">
                            {activity.campaign}
                          </span>
                        )}
                        {activity.donor && (
                          <span className="px-2 py-0.5 bg-slate-700/50 rounded-full">
                            {activity.donor}
                          </span>
                        )}
                      </div>

                      <span className="text-xs text-slate-500">
                        {formatTimeAgo(activity.timestamp)}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="p-12 text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-slate-800/50 rounded-full flex items-center justify-center">
              <Calendar className="w-8 h-8 text-slate-400" />
            </div>
            <h3 className="text-lg font-medium text-slate-300 mb-2">
              No recent activity
            </h3>
            <p className="text-slate-400">
              Activity from your campaigns will appear here
            </p>
          </div>
        )}
      </div>

      {activities.length > maxItems && (
        <div className="p-4 border-t border-slate-700/50 text-center">
          <button className="text-blue-400 hover:text-blue-300 text-sm font-medium transition-colors">
            View all activity ({activities.length - maxItems} more)
          </button>
        </div>
      )}
    </div>
  );
};

// Goal Progress Tracker
interface GoalTrackerProps {
  goals: {
    id: string;
    name: string;
    target: number;
    current: number;
    deadline: Date;
    category: string;
    priority: "high" | "medium" | "low";
  }[];
  className?: string;
}

export const GoalProgressTracker: React.FC<GoalTrackerProps> = ({
  goals,
  className = "",
}) => {
  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "high":
        return "border-red-500/30 bg-red-500/5";
      case "medium":
        return "border-yellow-500/30 bg-yellow-500/5";
      case "low":
        return "border-green-500/30 bg-green-500/5";
      default:
        return "border-slate-500/30 bg-slate-500/5";
    }
  };

  const getDaysLeft = (deadline: Date) => {
    const now = new Date();
    const diff = deadline.getTime() - now.getTime();
    const days = Math.ceil(diff / (1000 * 60 * 60 * 24));
    return days;
  };

  const sortedGoals = [...goals].sort((a, b) => {
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });

  return (
    <div className={clsx("card-base", className)}>
      <div className="p-6 border-b border-slate-700/50">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-white">Goal Progress</h3>
            <p className="text-sm text-slate-400">
              {goals.length} active goals
            </p>
          </div>
          <button className="button-ghost text-sm">Manage Goals</button>
        </div>
      </div>

      <div className="p-6 space-y-4">
        {sortedGoals.map((goal) => {
          const progress = (goal.current / goal.target) * 100;
          const daysLeft = getDaysLeft(goal.deadline);
          const isOverdue = daysLeft < 0;
          const isUrgent = daysLeft <= 7 && daysLeft >= 0;

          return (
            <div
              key={goal.id}
              className={clsx(
                "p-4 rounded-xl border",
                getPriorityColor(goal.priority),
              )}
            >
              <div className="flex items-start justify-between mb-3">
                <div>
                  <h4 className="font-medium text-white">{goal.name}</h4>
                  <p className="text-sm text-slate-400">{goal.category}</p>
                </div>

                <div className="text-right">
                  <div className="text-lg font-semibold text-white">
                    ${goal.current.toLocaleString()}
                  </div>
                  <div className="text-sm text-slate-400">
                    of ${goal.target.toLocaleString()}
                  </div>
                </div>
              </div>

              <div className="mb-3">
                <div className="flex justify-between text-sm mb-1">
                  <span className="text-slate-400">Progress</span>
                  <span className="text-white font-medium">
                    {progress.toFixed(1)}%
                  </span>
                </div>
                <div className="w-full bg-slate-700/50 rounded-full h-2">
                  <div
                    className={clsx(
                      "h-2 rounded-full transition-all duration-500",
                      progress >= 100
                        ? "bg-gradient-to-r from-green-500 to-green-400"
                        : progress >= 75
                          ? "bg-gradient-to-r from-blue-500 to-purple-500"
                          : progress >= 50
                            ? "bg-gradient-to-r from-yellow-500 to-orange-500"
                            : "bg-gradient-to-r from-red-500 to-red-400",
                    )}
                    style={{ width: `${Math.min(progress, 100)}%` }}
                  />
                </div>
              </div>

              <div className="flex items-center justify-between text-sm">
                <div className="flex items-center space-x-2">
                  <span
                    className={clsx(
                      "px-2 py-0.5 rounded-full text-xs font-medium",
                      goal.priority === "high"
                        ? "bg-red-500/20 text-red-300"
                        : goal.priority === "medium"
                          ? "bg-yellow-500/20 text-yellow-300"
                          : "bg-green-500/20 text-green-300",
                    )}
                  >
                    {goal.priority} priority
                  </span>
                </div>

                <span
                  className={clsx(
                    "text-sm font-medium",
                    isOverdue
                      ? "text-red-400"
                      : isUrgent
                        ? "text-yellow-400"
                        : "text-slate-400",
                  )}
                >
                  {isOverdue
                    ? "Overdue"
                    : daysLeft === 0
                      ? "Due today"
                      : daysLeft === 1
                        ? "1 day left"
                        : `${daysLeft} days left`}
                </span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// src/components/CampaignList.tsx - Complete campaign list component
import React, { useState, useEffect } from "react";

import CampaignQuickCard from "./CampaignQuickCard";
import LoadingSpinner from "./LoadingSpinner";
import { Campaign, CampaignFilters } from "../models/campaign";
import { campaignService } from "../services/campaignService";

interface CampaignListProps {
  onEditCampaign: (campaign: Campaign) => void;
  onViewCampaign: (campaign: Campaign) => void;
  onCreateCampaign: () => void;
}

const CampaignList: React.FC<CampaignListProps> = ({
  onEditCampaign,
  onViewCampaign,
  onCreateCampaign,
}) => {
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState<CampaignFilters>({});
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");

  useEffect(() => {
    loadCampaigns();
  }, [filters]);

  const loadCampaigns = async () => {
    try {
      setLoading(true);
      const data = await campaignService.getAllCampaigns(filters);
      setCampaigns(data);
    } catch (error) {
      console.error("Failed to load campaigns:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with create button and view toggle */}
      <div className="flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h3 className="text-lg font-semibold">
            Campaigns ({campaigns.length})
          </h3>
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setViewMode("grid")}
              className={`px-3 py-1 rounded ${
                viewMode === "grid"
                  ? "bg-blue-100 text-blue-700"
                  : "text-gray-600"
              }`}
            >
              Grid
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`px-3 py-1 rounded ${
                viewMode === "list"
                  ? "bg-blue-100 text-blue-700"
                  : "text-gray-600"
              }`}
            >
              List
            </button>
          </div>
        </div>

        <button
          onClick={onCreateCampaign}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
        >
          Create Campaign
        </button>
      </div>

      {/* Campaigns grid/list */}
      {campaigns.length === 0 ? (
        <div className="text-center py-12 text-gray-500">
          <p className="text-lg mb-4">No campaigns found</p>
          <button
            onClick={onCreateCampaign}
            className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Create Your First Campaign
          </button>
        </div>
      ) : (
        <div
          className={
            viewMode === "grid"
              ? "grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6"
              : "space-y-4"
          }
        >
          {campaigns.map((campaign) => (
            <CampaignQuickCard
              key={campaign.id}
              campaign={campaign}
              onClick={() => onViewCampaign(campaign)}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default CampaignList;

// src/components/CampaignModal.tsx - Modernized with unified dark theme
import clsx from "clsx";
import {
  X,
  Plus,
  AlertCircle,
  Calendar,
  DollarSign,
  Target,
  Users,
} from "lucide-react";
import React, { useState, useEffect } from "react";

import {
  Campaign,
  CampaignCreateRequest,
  CampaignUpdateRequest,
} from "../models/campaign";

interface CampaignModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (
    campaign: CampaignCreateRequest | CampaignUpdateRequest,
  ) => Promise<void>;
  initialData?: Campaign | null;
  mode: "create" | "edit";
}

type CampaignFormData = {
  id?: string;
  name: string;
  description?: string;
  goal: number;
  startDate: string;
  endDate: string;
  status: Campaign["status"];
  category: Campaign["category"];
  targetAudience?: string;
  tags?: string[];
  notes?: string;
};

export const CampaignModal: React.FC<CampaignModalProps> = ({
  isOpen,
  onClose,
  onSave,
  initialData,
  mode,
}) => {
  const [form, setForm] = useState<CampaignFormData>({
    name: "",
    description: "",
    goal: 0,
    startDate: "",
    endDate: "",
    status: "Planned",
    category: "General",
    targetAudience: "",
    tags: [],
    notes: "",
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [loading, setLoading] = useState(false);
  const [newTag, setNewTag] = useState("");
  const [currentStep, setCurrentStep] = useState(1);

  useEffect(() => {
    if (isOpen) {
      if (initialData && mode === "edit") {
        setForm({
          id: initialData.id,
          name: initialData.name,
          description: initialData.description || "",
          goal: initialData.goal,
          startDate: initialData.startDate,
          endDate: initialData.endDate,
          status: initialData.status,
          category: initialData.category,
          targetAudience: initialData.targetAudience || "",
          tags: initialData.tags || [],
          notes: initialData.notes || "",
        });
      } else {
        setForm({
          name: "",
          description: "",
          goal: 0,
          startDate: "",
          endDate: "",
          status: "Planned",
          category: "General",
          targetAudience: "",
          tags: [],
          notes: "",
        });
      }
      setErrors({});
      setNewTag("");
      setCurrentStep(1);
    }
  }, [isOpen, initialData, mode]);

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!form.name.trim()) {
      newErrors.name = "Campaign name is required";
    }

    if (form.goal <= 0) {
      newErrors.goal = "Goal must be greater than 0";
    }

    if (!form.startDate) {
      newErrors.startDate = "Start date is required";
    }

    if (!form.endDate) {
      newErrors.endDate = "End date is required";
    }

    if (
      form.startDate &&
      form.endDate &&
      new Date(form.startDate) >= new Date(form.endDate)
    ) {
      newErrors.endDate = "End date must be after start date";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >,
  ) => {
    const { name, value } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: name === "goal" ? Number(value) : value,
    }));

    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: "" }));
    }
  };

  const handleAddTag = () => {
    if (newTag.trim() && !form.tags?.includes(newTag.trim())) {
      setForm((prev) => ({
        ...prev,
        tags: [...(prev.tags || []), newTag.trim()],
      }));
      setNewTag("");
    }
  };

  const handleRemoveTag = (tagToRemove: string) => {
    setForm((prev) => ({
      ...prev,
      tags: prev.tags?.filter((tag) => tag !== tagToRemove) || [],
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      if (mode === "edit" && form.id) {
        const submitData: CampaignUpdateRequest = {
          id: form.id,
          name: form.name,
          description: form.description,
          goal: form.goal,
          startDate: form.startDate,
          endDate: form.endDate || "",
          status: form.status || "Planned",
          category: form.category || "General",
          targetAudience: form.targetAudience || "",
          tags: form.tags,
          notes: form.notes,
        };
        await onSave(submitData);
      } else {
        const submitData: CampaignCreateRequest = {
          name: form.name,
          description: form.description,
          goal: form.goal,
          startDate: form.startDate,
          endDate: form.endDate || "",
          status: form.status as "Planned" | "Active",
          category: form.category || "General",
          targetAudience: form.targetAudience || "",
          tags: form.tags,
          notes: form.notes,
        };
        await onSave(submitData);
      }
      onClose();
    } catch (error) {
      console.error("Failed to save campaign:", error);
      setErrors({ submit: "Failed to save campaign. Please try again." });
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  const categoryOptions = [
    {
      value: "General",
      label: "General",
      icon: "üìã",
      color: "from-blue-500 to-blue-600",
    },
    {
      value: "Emergency",
      label: "Emergency",
      icon: "üö®",
      color: "from-red-500 to-red-600",
    },
    {
      value: "Education",
      label: "Education",
      icon: "üéì",
      color: "from-green-500 to-green-600",
    },
    {
      value: "Healthcare",
      label: "Healthcare",
      icon: "üè•",
      color: "from-purple-500 to-purple-600",
    },
    {
      value: "Environment",
      label: "Environment",
      icon: "üå±",
      color: "from-emerald-500 to-emerald-600",
    },
    {
      value: "Community",
      label: "Community",
      icon: "üèòÔ∏è",
      color: "from-orange-500 to-orange-600",
    },
    {
      value: "Other",
      label: "Other",
      icon: "üìå",
      color: "from-gray-500 to-gray-600",
    },
  ];

  const statusOptions =
    mode === "edit" && initialData
      ? [
          {
            value: "Planned",
            label: "Planned",
            color: "bg-yellow-500/20 text-yellow-400",
          },
          {
            value: "Active",
            label: "Active",
            color: "bg-green-500/20 text-green-400",
          },
          {
            value: "Completed",
            label: "Completed",
            color: "bg-blue-500/20 text-blue-400",
          },
          {
            value: "Cancelled",
            label: "Cancelled",
            color: "bg-red-500/20 text-red-400",
          },
        ]
      : [
          {
            value: "Planned",
            label: "Planned",
            color: "bg-yellow-500/20 text-yellow-400",
          },
          {
            value: "Active",
            label: "Active",
            color: "bg-green-500/20 text-green-400",
          },
        ];

  const steps = [
    { id: 1, name: "Basic Info", icon: Target },
    { id: 2, name: "Details", icon: Calendar },
    { id: 3, name: "Settings", icon: Users },
  ];

  return (
    <div className="fixed inset-0 bg-black/70 backdrop-blur-sm flex items-center justify-center z-50 p-4 animate-fade-in">
      <div className="card-base w-full max-w-4xl max-h-[90vh] overflow-hidden animate-scale-in">
        {/* Header */}
        <div className="border-b border-surface/50 px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-white mb-1">
                {mode === "edit" ? "Edit Campaign" : "Create New Campaign"}
              </h2>
              <p className="text-slate-400">
                {mode === "edit"
                  ? "Update your campaign details"
                  : "Launch your next fundraising initiative"}
              </p>
            </div>
            <button
              onClick={onClose}
              className="p-2 rounded-xl text-slate-400 hover:text-white hover:bg-slate-700/50 transition-all duration-200"
            >
              <X className="w-6 h-6" />
            </button>
          </div>

          {/* Progress Steps */}
          <div className="flex items-center space-x-4 mt-6">
            {steps.map((step, index) => {
              const Icon = step.icon;
              const isActive = currentStep === step.id;
              const isCompleted = currentStep > step.id;

              return (
                <div key={step.id} className="flex items-center">
                  <div
                    className={clsx(
                      "flex items-center space-x-3 px-4 py-2 rounded-xl transition-all duration-200",
                      isActive && "bg-blue-500/20 border border-blue-500/30",
                      isCompleted &&
                        "bg-green-500/20 border border-green-500/30",
                      !isActive &&
                        !isCompleted &&
                        "bg-slate-800/50 border border-slate-700/50",
                    )}
                  >
                    <Icon
                      className={clsx(
                        "w-4 h-4",
                        isActive && "text-blue-400",
                        isCompleted && "text-green-400",
                        !isActive && !isCompleted && "text-slate-400",
                      )}
                    />
                    <span
                      className={clsx(
                        "text-sm font-medium",
                        isActive && "text-blue-300",
                        isCompleted && "text-green-300",
                        !isActive && !isCompleted && "text-slate-400",
                      )}
                    >
                      {step.name}
                    </span>
                  </div>
                  {index < steps.length - 1 && (
                    <div className="w-8 h-0.5 bg-slate-700/50 mx-2" />
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* Form Content */}
        <div
          className="overflow-y-auto custom-scrollbar"
          style={{ maxHeight: "calc(90vh - 200px)" }}
        >
          <form onSubmit={handleSubmit} className="p-8 space-y-8">
            {/* Error Display */}
            {errors.submit && (
              <div className="card-base p-4 border-red-500/30 bg-red-500/5">
                <div className="flex items-start space-x-3">
                  <AlertCircle className="w-5 h-5 text-red-400 mt-0.5 flex-shrink-0" />
                  <div>
                    <p className="text-red-300 font-medium text-sm">
                      Failed to save campaign
                    </p>
                    <p className="text-red-400/80 text-sm mt-1">
                      {errors.submit}
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Step 1: Basic Info */}
            {currentStep === 1 && (
              <div className="space-y-6">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  {/* Campaign Name */}
                  <div className="lg:col-span-2">
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Campaign Name *
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={form.name}
                      onChange={handleChange}
                      className={clsx(
                        "input-base text-lg h-12",
                        errors.name && "border-red-500/50 bg-red-500/5",
                      )}
                      placeholder="Enter an inspiring campaign name"
                      required
                    />
                    {errors.name && (
                      <p className="mt-2 text-sm text-red-400 flex items-center gap-2">
                        <AlertCircle className="w-4 h-4" />
                        {errors.name}
                      </p>
                    )}
                  </div>

                  {/* Description */}
                  <div className="lg:col-span-2">
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Description
                    </label>
                    <textarea
                      name="description"
                      value={form.description}
                      onChange={handleChange}
                      rows={4}
                      className="input-base resize-none"
                      placeholder="Describe your campaign goals, impact, and why people should contribute..."
                    />
                  </div>

                  {/* Goal */}
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Fundraising Goal *
                    </label>
                    <div className="relative">
                      <DollarSign className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
                      <input
                        type="number"
                        name="goal"
                        value={form.goal || ""}
                        onChange={handleChange}
                        className={clsx(
                          "input-base pl-12 text-lg font-semibold",
                          errors.goal && "border-red-500/50 bg-red-500/5",
                        )}
                        placeholder="25,000"
                        min={1}
                        required
                      />
                    </div>
                    {errors.goal && (
                      <p className="mt-2 text-sm text-red-400 flex items-center gap-2">
                        <AlertCircle className="w-4 h-4" />
                        {errors.goal}
                      </p>
                    )}
                  </div>

                  {/* Category */}
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Category *
                    </label>
                    <div className="grid grid-cols-2 gap-2">
                      {categoryOptions.map((option) => (
                        <button
                          key={option.value}
                          type="button"
                          onClick={() =>
                            setForm((prev) => ({
                              ...prev,
                              category: option.value as any,
                            }))
                          }
                          className={clsx(
                            "flex items-center space-x-3 p-3 rounded-xl transition-all duration-200 border text-left",
                            form.category === option.value
                              ? "border-blue-500/50 bg-blue-500/10 text-white"
                              : "border-slate-700/50 bg-slate-800/30 text-slate-300 hover:bg-slate-700/30",
                          )}
                        >
                          <span className="text-lg">{option.icon}</span>
                          <span className="font-medium text-sm">
                            {option.label}
                          </span>
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Step 2: Details */}
            {currentStep === 2 && (
              <div className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* Start Date */}
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Start Date *
                    </label>
                    <input
                      type="date"
                      name="startDate"
                      value={form.startDate}
                      onChange={handleChange}
                      className={clsx(
                        "input-base",
                        errors.startDate && "border-red-500/50 bg-red-500/5",
                      )}
                      required
                    />
                    {errors.startDate && (
                      <p className="mt-2 text-sm text-red-400 flex items-center gap-2">
                        <AlertCircle className="w-4 h-4" />
                        {errors.startDate}
                      </p>
                    )}
                  </div>

                  {/* End Date */}
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      End Date *
                    </label>
                    <input
                      type="date"
                      name="endDate"
                      value={form.endDate}
                      onChange={handleChange}
                      className={clsx(
                        "input-base",
                        errors.endDate && "border-red-500/50 bg-red-500/5",
                      )}
                      required
                    />
                    {errors.endDate && (
                      <p className="mt-2 text-sm text-red-400 flex items-center gap-2">
                        <AlertCircle className="w-4 h-4" />
                        {errors.endDate}
                      </p>
                    )}
                  </div>

                  {/* Status */}
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Status
                    </label>
                    <div className="flex flex-wrap gap-2">
                      {statusOptions.map((option) => (
                        <button
                          key={option.value}
                          type="button"
                          onClick={() =>
                            setForm((prev) => ({
                              ...prev,
                              status: option.value as any,
                            }))
                          }
                          className={clsx(
                            "px-4 py-2 rounded-xl text-sm font-medium border transition-all duration-200",
                            form.status === option.value
                              ? `${option.color} border-current`
                              : "text-slate-400 border-slate-700/50 hover:text-slate-300 hover:border-slate-600/50",
                          )}
                        >
                          {option.label}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Target Audience */}
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">
                      Target Audience
                    </label>
                    <input
                      type="text"
                      name="targetAudience"
                      value={form.targetAudience}
                      onChange={handleChange}
                      className="input-base"
                      placeholder="e.g., Parents, local businesses, alumni"
                    />
                  </div>
                </div>
              </div>
            )}

            {/* Step 3: Settings */}
            {currentStep === 3 && (
              <div className="space-y-6">
                {/* Tags */}
                <div>
                  <label className="block text-sm font-medium text-slate-300 mb-2">
                    Tags
                  </label>
                  <div className="flex flex-wrap gap-2 mb-3">
                    {form.tags?.map((tag, index) => (
                      <span
                        key={index}
                        className="inline-flex items-center px-3 py-1.5 rounded-full text-sm font-medium bg-blue-500/20 text-blue-300 border border-blue-500/30"
                      >
                        #{tag}
                        <button
                          type="button"
                          onClick={() => handleRemoveTag(tag)}
                          className="ml-2 text-blue-400 hover:text-blue-200 transition-colors"
                        >
                          <X className="w-3 h-3" />
                        </button>
                      </span>
                    ))}
                  </div>
                  <div className="flex gap-2">
                    <input
                      type="text"
                      value={newTag}
                      onChange={(e) => setNewTag(e.target.value)}
                      onKeyPress={(e) =>
                        e.key === "Enter" &&
                        (e.preventDefault(), handleAddTag())
                      }
                      className="flex-1 input-base"
                      placeholder="Add a tag (press Enter)"
                    />
                    <button
                      type="button"
                      onClick={handleAddTag}
                      disabled={!newTag.trim()}
                      className="button-secondary flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <Plus className="w-4 h-4" />
                      Add
                    </button>
                  </div>
                </div>

                {/* Internal Notes */}
                <div>
                  <label className="block text-sm font-medium text-slate-300 mb-2">
                    Internal Notes
                  </label>
                  <textarea
                    name="notes"
                    value={form.notes}
                    onChange={handleChange}
                    rows={4}
                    className="input-base resize-none"
                    placeholder="Internal notes for your team (not visible to donors)..."
                  />
                </div>
              </div>
            )}
          </form>
        </div>

        {/* Footer Actions */}
        <div className="border-t border-surface/50 px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              {currentStep > 1 && (
                <button
                  type="button"
                  onClick={() => setCurrentStep((prev) => prev - 1)}
                  className="button-ghost"
                >
                  ‚Üê Previous
                </button>
              )}
            </div>

            <div className="flex items-center gap-3">
              <button
                type="button"
                onClick={onClose}
                disabled={loading}
                className="button-ghost disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Cancel
              </button>

              {currentStep < steps.length ? (
                <button
                  type="button"
                  onClick={() => setCurrentStep((prev) => prev + 1)}
                  className="button-primary"
                >
                  Next ‚Üí
                </button>
              ) : (
                <button
                  onClick={handleSubmit}
                  disabled={loading}
                  className="button-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
                >
                  {loading && (
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                  )}
                  {loading
                    ? "Saving..."
                    : mode === "edit"
                      ? "Update Campaign"
                      : "Create Campaign"}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

