// Quick Message Composer
interface QuickMessageComposerProps {
  recipients: Array<{
    id: string;
    name: string;
    email: string;
    type: "donor" | "volunteer" | "staff";
  }>;
  templates: EmailTemplate[];
  onSend: (message: {
    recipients: string[];
    subject: string;
    content: string;
    template?: string;
  }) => void;
  className?: string;
}

export const QuickMessageComposer: React.FC<QuickMessageComposerProps> = ({
  recipients,
  templates,
  onSend,
  className = "",
}) => {
  const [selectedRecipients, setSelectedRecipients] = useState<string[]>([]);
  const [subject, setSubject] = useState("");
  const [content, setContent] = useState("");
  const [selectedTemplate, setSelectedTemplate] = useState<string>("");
  const [showTemplates, setShowTemplates] = useState(false);

  const handleTemplateSelect = (template: EmailTemplate) => {
    setSubject(template.subject);
    setContent(template.content);
    setSelectedTemplate(template.id);
    setShowTemplates(false);
  };

  const handleSend = () => {
    if (selectedRecipients.length === 0 || !subject || !content) return;

    onSend({
      recipients: selectedRecipients,
      subject,
      content,
      template: selectedTemplate || undefined,
    });

    // Reset form
    setSelectedRecipients([]);
    setSubject("");
    setContent("");
    setSelectedTemplate("");
  };

  const isValid =
    selectedRecipients.length > 0 && subject.trim() && content.trim();

  return (
    <div className={clsx("card-base p-6", className)}>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-lg font-semibold text-white">Quick Message</h3>
          <p className="text-sm text-slate-400">
            Send a message to your contacts
          </p>
        </div>

        <button
          onClick={() => setShowTemplates(!showTemplates)}
          className="button-ghost flex items-center space-x-2"
        >
          <Edit className="w-4 h-4" />
          <span>Use Template</span>
        </button>
      </div>

      {showTemplates && (
