// Donation History Component
interface Donation {
  id: string;
  amount: number;
  date: Date;
  campaign: string;
  method: "credit_card" | "bank_transfer" | "check" | "paypal";
  recurring: boolean;
  status: "completed" | "pending" | "failed";
}

interface DonationHistoryProps {
  donations: Donation[];
  className?: string;
  showFilters?: boolean;
}

export const DonationHistory: React.FC<DonationHistoryProps> = ({
  donations,
  className = "",
  showFilters = true,
}) => {
  const [filter, setFilter] = useState<"all" | "recurring" | "one-time">("all");
  const [sortBy, setSortBy] = useState<"date" | "amount">("date");

  const getMethodIcon = (method: string) => {
    switch (method) {
      case "credit_card":
        return "üí≥";
      case "bank_transfer":
        return "üè¶";
      case "check":
        return "üìù";
      case "paypal":
        return "üí∞";
      default:
        return "üí≥";
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed":
        return "text-green-400";
      case "pending":
        return "text-yellow-400";
      case "failed":
        return "text-red-400";
      default:
        return "text-slate-400";
    }
  };

  const filteredDonations = donations
    .filter((donation) => {
      if (filter === "recurring" && !donation.recurring) return false;
      if (filter === "one-time" && donation.recurring) return false;
      return true;
    })
    .sort((a, b) => {
      if (sortBy === "date") {
        return b.date.getTime() - a.date.getTime();
      } else {
        return b.amount - a.amount;
      }
    });

  return (
    <div className={clsx("card-base", className)}>
      <div className="p-6 border-b border-slate-700/50">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-white">
              Donation History
            </h3>
            <p className="text-sm text-slate-400">
              {donations.length} total donations
            </p>
          </div>

          {showFilters && (
            <div className="flex items-center space-x-3">
              <select
                value={filter}
                onChange={(e) => setFilter(e.target.value as any)}
                className="input-base text-sm py-2 px-3"
              >
                <option value="all">All Donations</option>
                <option value="recurring">Recurring Only</option>
                <option value="one-time">One-time Only</option>
              </select>

              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value as any)}
                className="input-base text-sm py-2 px-3"
              >
                <option value="date">Sort by Date</option>
                <option value="amount">Sort by Amount</option>
              </select>
            </div>
          )}
        </div>
      </div>

      <div className="max-h-96 overflow-y-auto custom-scrollbar">
        {filteredDonations.map((donation) => (
          <div
            key={donation.id}
            className="p-4 border-b border-slate-700/30 hover:bg-slate-800/30 transition-colors"
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="w-10 h-10 bg-slate-800/50 rounded-lg flex items-center justify-center">
                  <span className="text-lg">
                    {getMethodIcon(donation.method)}
                  </span>
                </div>

                <div>
                  <div className="flex items-center space-x-2">
                    <span className="font-semibold text-white">
                      ${donation.amount.toLocaleString()}
                    </span>
                    {donation.recurring && (
                      <span className="px-2 py-0.5 bg-blue-500/20 text-blue-300 text-xs rounded-full">
                        Recurring
                      </span>
                    )}
                  </div>
                  <div className="text-sm text-slate-400">
                    {donation.campaign} ‚Ä¢ {donation.date.toLocaleDateString()}
                  </div>
                </div>
              </div>

              <div
                className={clsx(
                  "text-sm font-medium",
                  getStatusColor(donation.status),
                )}
              >
                {donation.status.charAt(0).toUpperCase() +
                  donation.status.slice(1)}
              </div>
            </div>
          </div>
        ))}
      </div>

      {filteredDonations.length === 0 && (
        <div className="p-12 text-center">
          <div className="w-16 h-16 mx-auto mb-4 bg-slate-800/50 rounded-full flex items-center justify-center">
            <Gift className="w-8 h-8 text-slate-400" />
          </div>
          <h3 className="text-lg font-medium text-slate-300 mb-2">
            No donations found
          </h3>
          <p className="text-slate-400">
            {filter !== "all"
              ? `No ${filter === "recurring" ? "recurring" : "one-time"} donations to display.`
              : "This donor hasn't made any donations yet."}
          </p>
        </div>
      )}
    </div>
  );
};

