  // --------------------------

  private generateTimeSeriesData(
    startDate: string,
    endDate: string,
    totalRaised: number,
    totalDonors: number,
  ) {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const days = Math.ceil(
      (end.getTime() - start.getTime()) / (1000 * 60 * 60 * 24),
    );
    const data = [];
    let cumulativeRaised = 0;
    let cumulativeDonors = 0;

    for (let i = 0; i < days; i++) {
      const currentDate = new Date(start);
      currentDate.setDate(start.getDate() + i);

      const dailyRaised = Math.round(
        (totalRaised * 0.8 * Math.random() + totalRaised * 0.2) / days,
      );
      const dailyDonors = Math.round(
        (totalDonors * 0.8 * Math.random() + totalDonors * 0.2) / days,
      );

      cumulativeRaised += dailyRaised;
      cumulativeDonors += dailyDonors;

      data.push({
        date: currentDate.toISOString().split("T")[0],
        dailyRaised,
        dailyDonors,
        cumulativeRaised: Math.min(cumulativeRaised, totalRaised),
        cumulativeDonors: Math.min(cumulativeDonors, totalDonors),
      });
    }

    return data;
  }

